```javascript

/**
 *  O(n)
 *  O(n)
 */
/**
 * @param {string} s
 * @return {number}
 */
var lengthOfLongestSubstring = function(s) {
    var map = {};
    var left = sum = 0;
    var right = -1;

    while(right++ < s.length - 1 && left <= s.length) {
        //   
        // 
        if(map[s[right]] && map[s[right]] >= left) {
            left = map[s[right]];
            map[s[right]] = right + 1;
        }
        //  map 
        else {
            map[s[right]] = right + 1;
            if(right - left + 1 > sum) {
                sum = right - left + 1;
            } 
        }
    }
    return sum;
};

```

