/**
 * @param {string} str
 * @return {string}
 */
var toLowerCase = function(str) {
    var res = '';
    for(var i = 0; i < str.length; i++) {
        s = str[i];
        if (str[i] >= 'A' && str[i] <= 'Z') {
            s = String.fromCharCode(parseInt(str[i].charCodeAt() + 32, 10));
        }
        res = res + s;
    }

    delete i;
    return res;
};
```

