/**
 * @param {number[]} nums1
 * @param {number} m
 * @param {number[]} nums2
 * @param {number} n
 * @return {void} Do not return anything, modify nums1 in-place instead.
 */
var merge = function(nums1, m, nums2, n) {
    var i = 0;
    var j = 0;
    while(i < m && j < n) {
        if (nums1[i] > nums2[j]) {
            var t = nums1[i];
            nums1[i] = nums2[j];
            nums2[j] = t;

            for(var k = j; k < n; k++) {
                if (nums2[k] > nums2[k + 1]) {
                    t = nums2[k];
                    nums2[k] = nums2[k + 1];
                    nums2[k + 1] = t;
                }
            }
        }
        i++;
    }
    while(j<n) {
        nums1[i++] = nums2[j++];
    }
};
```

